document.addEventListener("DOMContentLoaded",function(){const privacyBtn=document.querySelector('#privacyBtn')
const privacyText=document.querySelector('#privacy')
const privacyBtnClose=document.querySelector('#privacyBtnClose')
const termsBtn=document.querySelector('#termsBtn')
const termsText=document.querySelector('#terms')
const termsBtnClose=document.querySelector('#termsBtnClose')
isModal=function(wrapper){if(wrapper=='privacy'){privacyText.classList.toggle('privacy--active')}else if(wrapper=='terms'){termsText.classList.toggle('terms--active')}};termsBtn.addEventListener('click',function(){isModal('terms')})
termsBtnClose.addEventListener('click',function(){isModal('terms')});privacyBtn.addEventListener('click',function(){isModal('privacy')});privacyBtnClose.addEventListener('click',function(){isModal('privacy')})})